package term1;

public class Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=-5, b=10, c=100;
		//1 display if a is equal to -5
		System.out.println(a==-5);
		//2 display is a is diff from -5
		System.out.println(a!=-5);
		//3 display if b is lower than 10
		System.out.println(b<10);
		//4 display if b is lower than or equal to 10
		System.out.println(b<=10);
		//5 display if c is greates than 50
		System.out.println(c>50);
		//6 display if c plus one is equal to 101
		System.out.println(c+1 ==101);
		

	}

}
